// Get raw text from Gemini or any node
let raw =
  items[0].json?.content?.parts?.[0]?.text ||
  items[0].json?.text ||
  JSON.stringify(items[0].json);

// Convert everything to string
raw = String(raw);

// Remove unwanted technical words/keys
raw = raw.replace(/role:.*|finishReason:.*|index:.*|model|STOP/gi, "");

// Remove escaped newlines and extra spaces
raw = raw
  .replace(/\\n/g, " ")
  .replace(/\n/g, " ")
  .replace(/\s+/g, " ")
  .trim();

// Build clean output
let output = "";
output += "CAR INFORMATION SUMMARY\n\n";
output += raw + "";
output += " CONCLUSION:- ";
output += "Information above is converted into clean, readable text. ";
output += "Always verify details with official sources.";

return [
  {
    json: {
      structured_output: output
    }
  }
];

// Extract Instagram profile data from API JSON response
const data = $input.first().json;

// Access the user data from the API response
const userData = data.data?.user || {};

// Timeline media (posts/reels)
const mediaEdges = userData.edge_owner_to_timeline_media?.edges || [];

// Get last 10 posts
const last10Posts = mediaEdges.slice(0, 10).map(edge => {
  const node = edge.node;
  return {
    id: node.id,
    shortcode: node.shortcode,
    isVideo: node.is_video,
    caption: node.edge_media_to_caption?.edges?.[0]?.node?.text || '',
    likeCount: node.edge_liked_by?.count || 0,
    commentCount: node.edge_media_to_comment?.count || 0,
    viewCount: node.video_view_count || 0,
    takenAt: new Date(node.taken_at_timestamp * 1000).toISOString()
  };
});

// Engagement calculations
const totalLikes = last10Posts.reduce((a, b) => a + b.likeCount, 0);
const totalComments = last10Posts.reduce((a, b) => a + b.commentCount, 0);

const avgLikes = last10Posts.length ? Math.round(totalLikes / last10Posts.length) : 0;
const avgComments = last10Posts.length ? Math.round(totalComments / last10Posts.length) : 0;

// Most liked post
const mostLikedPost = last10Posts.reduce(
  (max, post) => post.likeCount > max.likeCount ? post : max,
  last10Posts[0] || {}
);

// Most viewed reel
const mostViewedReel = last10Posts
  .filter(p => p.isVideo)
  .reduce(
    (max, post) => post.viewCount > max.viewCount ? post : max,
    last10Posts[0] || {}
  );

// Bio links
const bioLinks = userData.bio_links?.map(l => l.url) || [];

// Structure the profile data
const profileData = {
  // Basic Info
  username: userData.username || '',
  fullName: userData.full_name || '',
  biography: userData.biography || '',
  profilePicUrl: userData.profile_pic_url_hd || userData.profile_pic_url || '',
  externalUrl: userData.external_url || '',
  bioLinks,

  // Account Status
  isVerified: userData.is_verified || false,
  isPrivate: userData.is_private || false,
  isBusiness: userData.is_business_account || false,
  isProfessional: userData.is_professional_account || false,
  category: userData.category_name || '',

  // Counts
  followersCount: userData.edge_followed_by?.count || 0,
  followingCount: userData.edge_follow?.count || 0,
  postsCount: userData.edge_owner_to_timeline_media?.count || 0,

  // Contact (only if public)
  businessEmail: userData.business_email || '',
  businessPhone: userData.business_phone_number || '',
  businessAddress: userData.business_address_json || {},

  // Engagement
  avgLikes,
  avgComments,

  // Highlights
  hasHighlights: userData.highlight_reel_count > 0,

  // Content Insights
  last10Posts,
  mostLikedPost,
  mostViewedReel
};

return [{ json: profileData }];

